app.get('/orderdelivered',(req,res)=>{
  db.collection('liveusers')
  .where('user',"==",req.query.user)
  .get().then((docs)=>{
    docs.forEach((doc)=>{
      doc.ref.delete();
    })
  })
  db.collection('Orders')
  .where('user',"==",req.query.user)
  .where('status',"==","1")
  .get().then((docs)=>{
    docs.forEach((doc)=>{
      doc.ref.delete();
    })
  })
  setTimeout(function() {
    res.redirect('/adminmain');
  }, 3000);
})

app.get('/addtocart', (req, res) => {
  db.collection('Orders')
  .where('user',"==",req.query.user)
  .where('item',"==",req.query.item)
  .get().then((docs)=>{
    if(docs._size==0)
    {
      db.collection('Orders')
      .add({
          user : req.query.user,
          item : req.query.item,
          quantity : req.query.quantity,
          status : '0'
      })
    }
    else{
      docs.forEach((doc)=>{
        if(req.query.quantity==0)
        {
          doc.ref.delete()
        }
        else{
          doc.ref.update({
            quantity : req.query.quantity,
          })
        }
      })
    }
  })
});
app.get('/changecart', (req, res) => {
  var user=req.query.user;
  db.collection('Orders')
  .where('user',"==",req.query.user)
  .where('item',"==",req.query.item)
  .get().then((docs)=>{
      docs.forEach((doc)=>{
        if(req.query.quantity==0)
        {
          doc.ref.delete()
        }
        else{
          doc.ref.update({
            quantity : req.query.quantity,
          })
        }
      })
      setTimeout(function() {
        res.redirect('/cart?user=' + user);
      }, 2000);
  })
});

