app.get('/loginsubmit', async (req, res) => {
  try {
    const querySnapshot = await db
      .collection('Login')
      .where('user', '==', req.query.user)
      .get();

    if (querySnapshot.size === 1) {
      const doc = querySnapshot.docs[0];
      if (doc.data().pass === req.query.pass) {
        var user = req.query.user;
        res.redirect("/home?user="+user);
      } else {
        res.sendFile(__dirname + '/logininvalid.html');
      }
    }
  } catch (error) {
    console.error(error);
    res.status(500).json({ error: 'Internal Server Error' });
  }
});

app.get('/signupsubmit', async (req, res) => {
  try {
    await db.collection('Login').add({
      user: req.query.user,
      pass: req.query.pass,
    });
    res.sendFile(__dirname + '/signupsuccess.html');
  } catch (error) {
    console.error(error);
    res.status(500).json({ error: 'Internal Server Error' });
  }
});


